// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.9.0;

import "./Roles.sol";

contract logisticsCompany {
  using Roles for Roles.Role;

  event logisticsCompanyAdded(address indexed _account);
  event logisticsCompanyRemoved(address indexed _account);

  Roles.Role private logisticsCompanysList;

  constructor() public {
    logisticsCompanysList.addRole(msg.sender);
    emit logisticsCompanyAdded(msg.sender);
  }

  ///@dev Modifiers for logisticsCompany.
  modifier onlylogisticsCompany() {
    require(islogisticsCompany(msg.sender));
    _;
  }
  /*-----------------------------*/

  ///@dev logisticsCompany Utility functions.
  function islogisticsCompany(address _account) public view returns (bool) {
    return logisticsCompanysList.hasRole(_account);
  }

  function addlogisticsCompany(address _account) public onlylogisticsCompany {
    logisticsCompanysList.addRole(_account);
    emit logisticsCompanyAdded(_account);
  }

  function removelogisticsCompany() public {
    logisticsCompanysList.removeRole(msg.sender);
    emit logisticsCompanyRemoved(msg.sender);
  }
  /*-----------------------------*/

}
